    plt.contourf(X, Y, fun([X, Y]), 8, alpha=.75, cmap='jet')
    C = plt.contour(X, Y, fun([X, Y]), 8, colors='black') 
    
    for i in range(len(points)):
        xs.append(points[i][0])
